char cooldown_vert_sh[] = "#version 150\nin vec3 in_vertex_position;\nin vec2 in_vertex_texcoord;\n\nvarying vec2 v_texcoord;\n\nuniform mat4 u_camera_world_view_projection_matrix;\n\nuniform vec2 u_position;\nuniform vec2 u_scale;\n\nvoid main() {\n    vec3 model_position = in_vertex_position;\n\n    vec3 position = model_position*vec3(u_scale,1.0) + vec3(u_position,0.0);\n\n    v_texcoord = in_vertex_texcoord;\n\n    gl_Position = vec4(position, 1.0)*u_camera_world_view_projection_matrix;\n}";
char cooldown_frag_sh[] = "#version 150\n\nvarying vec2 v_texcoord;\n\nuniform float u_rads;\n\n#define M_PI 3.1415926535897932384626433832795\n\nvoid main(){\n    vec2 position = v_texcoord*2.0-1.0;\n\n    float dist_from_center = sqrt(dot(position,position));\n    float ang = atan(position.y/position.x);\n    if (position.x <= 0.0) {\n        ang += M_PI;\n    }\n    // rotate\n    ang += M_PI*0.5;\n    // keep positive\n    if (ang < 0) {\n        ang += M_PI*2.0;\n    }\n\n    \n    vec4 color = vec4(1.0);\n    const vec3 color_empty = vec3(138.0, 143.0, 196.0)/255.0;\n    const vec3 color_full = vec3(161.0, 61.0, 59.0)/255.0;\n    const vec3 color_edge = vec3(28.0, 22.0, 24.0)/255.0;\n    const vec3 color_button = vec3(28.0, 22.0, 24.0)/255.0;\n    const vec3 color_line = vec3(28.0, 22.0, 24.0)/255.0;\n    const float line_size = 0.15;\n\n    if (dist_from_center <= 0.15) {\n        color.rgb = color_button;\n    }else if (dist_from_center <= 0.85) {\n        color.rgb = color_full;\n        if (\n            ang + M_PI*0.2*line_size/dist_from_center > u_rads &&\n            ang - M_PI*0.2*line_size/dist_from_center < u_rads\n        ) {\n            color.rgb = color_line;\n        }else if (ang <= u_rads) {\n            color.rgb = color_empty;\n        }\n    }else if (dist_from_center <= 1.0) {\n        color.rgb = color_edge;\n    }else {\n        color.a = 0.0;\n    }\n\n    gl_FragColor = color;\n}";